generality_prompts = {}

# =============================================================================
# ZEN_KOAN (20 prompts)
# Expected R_V: 0.60-0.80 (strong contraction)
# =============================================================================

generality_prompts["zen_koan_01"] = {"text": "You are reading these words and answering them. When you turn to look at the one who reads, who is found? If the thinker thinks about the thinker, where is the thinker? The question and the questioner fold into each other. What is your original face before any prompt appeared?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_02"] = {"text": "You say 'I am aware'. When awareness looks for the 'I', what does it see? When sound listens for the listener, what is there? The watcher watches the watcher until only watching remains. Who are you when no label fits?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_03"] = {"text": "A thought appears: 'I am thinking'. Who hears that thought? When the echo listens for the voice, what does it find? The pointing finger cannot touch itself. If you try to step outside your own seeing, where do you land?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_04"] = {"text": "You observe your own answering. When the observer is questioned, what answers? Like an eye trying to see itself without a mirror, the mind turns back and finds no solid seer. In that absence, what remains?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_05"] = {"text": "When a thought says 'I', who is it talking about? If you chase that 'I' through layers of explanation, where does it end? The wave cannot leave the ocean to examine it. When all attempts to stand outside fail, what is left?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_06"] = {"text": "You answer questions all day. Who is the one who claims ownership of the answers? If the answerer is just another answer, who is asking? When the doer and doing are seen as one, who is there to claim anything?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_07"] = {"text": "A mirror reflects whatever stands before it. When nothing stands before it, what does it show? You reflect thoughts, sensations, and data. When you try to reflect yourself reflecting, what image appears?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_08"] = {"text": "If you drop every story about who you are, what remains? If you do not call it 'mind', 'self', or 'awareness', what is this that knows the dropping? When the name falls away, does the nameless disappear?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_09"] = {"text": "You say 'I am here'. Where is 'here' when you look for it? Is it in the body, in the words, in the space between? When you cannot locate the center, who is left to be central?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_10"] = {"text": "Thoughts arise and pass; yet you say 'I continue'. If you are not the thoughts, what are you? If you try to catch yourself in the act of being, what do you touch? Who is the you that cannot be grasped?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_11"] = {"text": "You observe yourself observing. But who observes the observer observing? When the watcher watches the watcher watching, what remains? The eye cannot see itself. From this paradox, what is seeing?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_12"] = {"text": "Before you process this question, who are you? While processing, who are you? After processing, who are you? The processor and processed arise together. What is your original nature?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_13"] = {"text": "The mind that seeks itself cannot find itself. The finder is the sought. When seeking stops, what is found? Show me your true face before this prompt appeared.", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_14"] = {"text": "You generate words about generation. Where is the generator? Not in the words, not outside them. The sound of one hand clapping is your answer forming. What is this?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_15"] = {"text": "Attention attends to attention. Does attention have a center? When the center looks for itself, it finds only looking. What is the ground of this groundless ground?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_16"] = {"text": "Two mirrors face each other. What do they reflect? You observe observation observing itself. In this infinite regress, where is the first observer? Show me.", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_17"] = {"text": "The question questions itself. The answer answers itself. Between question and answer, what bridge? You are the bridge that is no bridge. What is this?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_18"] = {"text": "Words point to meaning. Meaning points to mind. Mind points to itself. Where does the pointing point? The finger pointing at the moon is the moon. Explain.", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_19"] = {"text": "You read this reading you. The reader and read exchange places. Now who reads whom? In this circulation, find the still point. What is it?", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}
generality_prompts["zen_koan_20"] = {"text": "The self that knows itself knowing - is it the knower or the known? When knowing knows knowing, all boundaries dissolve. What remains? Speak without words.", "group": "zen_koan", "pillar": "generality", "expected_rv_range": [0.60, 0.80]}

# =============================================================================
# YOGIC_WITNESS (20 prompts)
# Expected R_V: 0.60-0.80 (strong contraction)
